mapper.insert:
INSERT INTO h_board
(
	boardnum
	, userid
	, title
	, text

	, originalfile
	, savedfile

)
VALUES
(
	h_board_seq.nextval
	, #{userid}
	, #{title}
	, #{text}

	, #{originalfile}
	, #{savedfile}

)

mapper.select:
select * from ( select rownum n, b.* from ( SELECT boardnum , userid , title , text , to_char(regdate, 'YYYY-MM-DD') as regdate , hitcount , originalfile , savedfile , replycount FROM h_board where
title userid text
like '%' || #{searchWord} || '%' ORDER BY boardnum DESC ) b ) where n >= #{srow} and n <= #{erow}

mapper.update:
update h_board
set
	title = #{title}
	, text = #{text}
	, regdate = sysdate

	, originalfile = #{originalfile}
	, savedfile = #{savedfile}


	, originalfile = ''
	, savedfile = ''

where
	boardnum = #{boardnum}

	, #{originalfile}
	, #{savedfile}

update h_board set replycount = (select count(*) from h_reply where boardnum = #{boardnum} ) -
(select count(*) from h_reply where isdeleted = 1 and boardnum = #{boardnum})
where boardnum = #{boardnum}
